import React from "react";
import List from "@material-ui/core/List";
import TodoForm from "./TodoForm";
import Todo from "./Todo";
import SearchAppBar from "./SearchAppBar";

export default class TodoList extends React.Component {
  state = {
    todos: [],
    todosToShow: [],
    filterText: '',
  };

  getInitialState = () => {
    return {
      todos: this.state.todos
    };
  };
  addTodo = todo => {
    this.setState({
      todos: [todo, ...this.state.todos]
    });
  };

  handleDelete = id => {
    this.setState({
      todos: this.state.todos.map(todo => {
        // debugger;
        if (todo.id === id) {
          return {
            ...todo,
            isDeleted: true
          };
        } else {
          return todo;
        }
      })
    });
  };

  handleToggleDone = id => {
    this.setState({
      todos: this.state.todos.map(todo => {
        if (todo.id === id) {
          return {
            ...todo,
            done: !todo.done
          };
        } else {
          return todo;
        }
      })
    });
  };

  handleSearch = event => {
      var todosToShow, searchValue;
      debugger;
    this.setState({
        filterText: event.target.value.toLowerCase()
    });
    if(this.state.filterText.length > 0 ){
        todosToShow = this.state.todos.filter(function(el) {
            searchValue = el.text.toLowerCase();

            return searchValue.indexOf(this.state.filterText) !== -1;
            });
    }else{
        todosToShow = this.state.todos;
    }



    this.setState({
        todosToShow: todosToShow
    });
  };

  render() {

    return (
      <div>
        <TodoForm onSubmit={this.addTodo} />
        <SearchAppBar handleSearch={this.handleSearch} />
        <button>show done</button>
        <button>show done</button>
        <button>delete all complete</button>
        {this.state.todos.map(todo =>
          todo.isDeleted ? (
            ""
          ) : (
            <List key={todo.id}>
              <Todo
                todo={todo}
                deleteTodo={() => this.handleDelete(todo.id)}
                toggleDone={() => this.handleToggleDone(todo.id)}
              />
            </List>
          )
        )}
      </div>
    );
  }
}
