import React from "react";
import { BrowserRouter as Router, Route} from "react-router-dom";

import './App.css';

import TodoForm from './TodoForm'

class App extends React.Component {

    render() {
        return (
            <div className="container">
                <Router>
                    <Route exact path="/" component={Login} />
                    <Route exact path="/to-do" component={TodoForm} />
                    {/* <SimpleList/>
                    <ComplexList/> */}

                </Router>
            </div>
        );
    }

}

class Login extends React.Component {
    constructor(props) {
        super(props);
        this.signIn = this.signIn.bind(this);
        this.handleEmailChange = this.handleEmailChange.bind(this);
        this.handlePasswordChange = this.handlePasswordChange.bind(this);
        this.state = {
            email: '',
            password: ''
        };
    }


    signIn() { 
        if(this.validateForm()){
            //send info to server to check here...

            console.log("loging by:" + this.state.email);

            // if status = OK: then...
            this.props.history.push('/to-do')
        }else{

        }
    }

    handleEmailChange(e) {
        this.setState({ email: e.target.value })
    }

    handlePasswordChange(e) {
        this.setState({ password: e.target.value })
    }

    validateForm() {
        debugger;
        return this.state.email.length > 0 && this.state.password.length > 0;
    }

    render() {
        return (
            <div id="idoFormLogin" className="container-login">
                <form >
                    <h2 style={{textAlign: 'center'}}>Please login</h2>
                    <label><b>Username</b></label>
                    <input type="text" onChange={this.handleEmailChange} placeholder="Enter Username" required/>
                    <label><b>Password</b></label>
                    <input type="password" onChange={this.handlePasswordChange} placeholder="Enter Password" required/>
                    <button onClick={this.signIn}>
                        Sign in...
                    </button>
                </form>
            </div>
        )
    }
}

class ToDo extends React.Component {
    render() {
        return (
            <div>
                To do list Here
            </div>
        )
    }
}

const list = ['a', 'b', 'c'];

const SimpleList = () => (
  <ul>
    {list.map(item => {
      return <li key={item}>{item}</li>;
    })}
  </ul>
);

const listComplex = [
    {
      id: 'a',
      firstname: 'Robin',
      lastname: 'Wieruch',
      year: 1988,
    },
    {
      id: 'b',
      firstname: 'Dave',
      lastname: 'Davidds',
      year: 1990,
    },
  ];

  const ComplexList = () => (
    <ul>
      {listComplex.map(item => (
        <li key={item.id}>
          <div>{item.id}</div>
          <div>{item.firstname}</div>
          <div>{item.lastname}</div>
          <div>{item.year}</div>
        </li>
      ))}
    </ul>
  );

export default App;
