import React from "react";
import List from "@material-ui/core/List";
import TodoForm from "./TodoForm";
import Todo from "./Todo";
import SearchAppBar from "./SearchAppBar";

export default class TodoList extends React.Component {
  state = {
    todos: [],
    filterText: '',
  };

  getInitialState = () => {
    return {
      todos: this.state.todos
    };
  };
  addTodo = todo => {
    this.setState({
      todos: [todo, ...this.state.todos]
    });
  };

  handleDelete = id => {
    this.setState({
      todos: this.state.todos.map(todo => {
        // debugger;
        if (todo.id === id) {
          return {
            ...todo,
            isDeleted: true
          };
        } else {
          return todo;
        }
      })
    });
  };

  handleToggleDone = id => {
    this.setState({
      todos: this.state.todos.map(todo => {
        if (todo.id === id) {
          return {
            ...todo,
            done: !todo.done
          };
        } else {
          return todo;
        }
      })
    });
  };

  handleSearch = event => {

    this.setState({
        filterText: event.target.value.toLowerCase()
    });

  };

  render() {
    var todosToShows, searchValue, filterText = this.state.filterText;

    if(filterText.length > 0 ){
        todosToShows = this.state.todos.filter(function(el) {
            searchValue = el.text.toLowerCase();

            return searchValue.indexOf(filterText) !== -1;
            });
    }else{
        todosToShows = this.state.todos;
    }
    return (
      <div>
        <TodoForm onSubmit={this.addTodo} />
        <SearchAppBar handleSearch={this.handleSearch} />
        {/* <button>show done</button>
        <button>show done</button>
        <button>delete all complete</button> */}
        {todosToShows.map(todo =>
          todo.isDeleted ? (
            ""
          ) : (

            <List key={todo.id} className="listTodo">
              <Todo
                todo={todo}
                deleteTodo={() => this.handleDelete(todo.id)}
                toggleDone={() => this.handleToggleDone(todo.id)}
              />
            </List>

          )
        )}
      </div>
    );
  }
}
