import React from "react";
import shortid from "shortid";
import ListItem from "@material-ui/core/ListItem";
import ListItemSecondaryAction from "@material-ui/core/ListItemSecondaryAction";
import ListItemText from "@material-ui/core/ListItemText";
import Checkbox from "@material-ui/core/Checkbox";
import IconButton from "@material-ui/core/IconButton";
import DeleteIcon from "@material-ui/icons/Delete";
import AddIcon from "@material-ui/icons/Add";
import Bookmark from "@material-ui/icons/Bookmark";
import TextField from '@material-ui/core/TextField';

export default class TodoForm extends React.Component {
  state = {
    text: "",
    dueDate: "",
    priority: false
  };

  handleChange = event => {
    this.setState({
      [event.target.name]: event.target.value
    });
  };

  handleDueDateChange = event => {
    this.setState({
      dueDate: event.target.value
    });
  };

  handleSetPriority = event => {
    this.setState({
      priority: !this.state.priority
    });
  };

  handleSubmit = event => {
    event.preventDefault();
    this.props.onSubmit({
      id: shortid.generate(),
      text: this.state.text,
      dueDate: this.state.dueDate,
      priority: this.state.priority,
      complete: false,
      isDeleted: false
    });
    this.setState({
        text: '',
        dueDate: "",
        priority: false
    });
  };

  render() {
    return (
      <form onSubmit={this.handleSubmit} style={{display: 'flex'}}>
        {/* <ListItem dense button className="listItem">
            <input
            name="text"
            value={this.state.text}
            onChange={this.handleChange}
            placeholder="add new to do item"
            />
            <ListItemSecondaryAction>
            <IconButton aria-label="Add" onClick={this.handleSubmit}>
                <DeleteIcon />
            </IconButton>
            </ListItemSecondaryAction>
        </ListItem> */}
        <input
          type="text"
          style={{display: 'flex'}}
          name="text"
          value={this.state.text}
          onChange={this.handleChange}
          placeholder="add new to do item"
        />
        <TextField
        id="date"
        label="Due date"
        type="date"
        InputLabelProps={{
          shrink: true,
        }}
        onChange={this.handleDueDateChange}
      />
        <IconButton 
            aria-label="High priority" 
            style={{color: this.state.priority ? 'red' : ''}}
            onClick={this.handleSetPriority}
            tooltip="set priority">
          <Bookmark />
        </IconButton>
        <IconButton aria-label="Add" onClick={this.handleSubmit}>
          <AddIcon />
        </IconButton>
      </form>
    );
  }
}
